
public class UsoOperadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num1=7;
		
		int num2=5;
		
		int resultado=num1+num2;
		
		System.out.println(resultado); 
		
		num2++; //incrementé en 1 a la variable
		
		System.out.println(num2);
		
		System.out.println(resultado);
		
		/*ojo al piojo con eso último: imprime lo que vale resulotado
		 * que era y sigue siendo 12 aunque ñuego variè num2, ahora harè otra vez 
		 * resultado a ver que pasa  
		 */
		resultado=num1+num2;
		System.out.println(resultado);
		/*ahora SI actualizò el valor resultado a 13*/
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	 	

	}

}
